  `F_ParentId` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '父级id',
  `F_Platform` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'PC:网页端 APP:手机端 MOD:模板',
  PRIMARY KEY (`F_Id`) USING BTREE
